"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[5223],{9524:e=>{e.exports=JSON.parse('{"tag":{"label":"platform","permalink":"/reference/tags/platform","allTagsPath":"/reference/tags","count":8,"items":[{"id":"architectural-layers","title":"Architectural Layers","description":"FOSPS is structured using a three-layer architecture that separates concerns and enables modularity.","permalink":"/reference/architectural-layers"},{"id":"connectors","title":"Connectors","description":"Connectors are modules responsible for modularizing access to external Trusted Sources of Information (TSI) and performing necessary format transformations into FHIR format.","permalink":"/reference/connectors"},{"id":"fhir-server","title":"FHIR Server","description":"The FHIR Server is an internal component of FOSPS, often implemented using the HAPI FHIR JPA Server Starter, responsible for managing FHIR resources.","permalink":"/reference/fhir-server"},{"id":"fosps","title":"FOSPS","description":"FOSPS is the overall software platform designed using a microservices-based, three-layer architecture to support the G-lens solution.","permalink":"/reference/fosps"},{"id":"g-lens","title":"G-lens Solution","description":"G-lens is the pioneering solution supported by FOSPS that empowers patients to access trustworthy, personalized Electronic Product Information (ePI).","permalink":"/reference/g-lens"},{"id":"istio","title":"Istio Service Mesh","description":"Istio is the service mesh used by FOSPS for network management, providing traffic control, security, and acting as the Service Mesh and Gateway.","permalink":"/reference/istio"},{"id":"kubernetes-deployment","title":"Kubernetes & Helm","description":"FOSPS uses Kubernetes (k8s) as the underlying orchestration technology and Helm Charts as the packaging system for deploying components.","permalink":"/reference/kubernetes-deployment"},{"id":"openapi","title":"OpenAPI & Swagger","description":"FOSPS uses OpenAPI Specification (OAS) as the standard for API documentation, exposed through Swagger UI deployment.","permalink":"/reference/openapi"}],"unlisted":false}}')}}]);