"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[8182],{4836:e=>{e.exports=JSON.parse('{"tag":{"label":"focusing","permalink":"/reference/tags/focusing","allTagsPath":"/reference/tags","count":9,"items":[{"id":"attention-modification","title":"Attention Detail Modification","description":"Attention Detail Modification is the specific non-content changes applied by Lenses through CSS class manipulation to guide user attention without altering regulated ePI text.","permalink":"/reference/attention-modification"},{"id":"f-epi","title":"f(ePI) - Focused ePI","description":"f(ePI) (Focused ePI) is the final personalized output of the Focusing process, adapted for a specific patient\'s context through Lens execution.","permalink":"/reference/f-epi"},{"id":"focusing","title":"Focusing","description":"Focusing is the process defined as \\"Adapting information to the context of the end user for effective and optimal understanding of the information\\" without modifying the regulated content itself.","permalink":"/reference/focusing"},{"id":"focusing-flows","title":"Focusing Flows","description":"Focusing Flows are the four defined methods for invoking the Focusing process in FOSPS, based on how ePI and IPS/PV data are provided.","permalink":"/reference/focusing-flows"},{"id":"focusing-manager","title":"Focusing Manager","description":"The Focusing Manager is the central orchestration component of the Focusing module that coordinates all steps of the personalization workflow.","permalink":"/reference/focusing-manager"},{"id":"lee","title":"LEE (Lens Execution Environment)","description":"The LEE (Lens Execution Environment) is the service that executes Lens logic on p(ePI) using patient contextual data to produce the final f(ePI).","permalink":"/reference/lee"},{"id":"lens","title":"Lens","description":"A Lens is a conceptual piece of code packaged as a FHIR Library resource that encodes clinical or context-specific knowledge, applying transformation rules based on patient data.","permalink":"/reference/lens"},{"id":"p-epi","title":"p(ePI) - Preprocessed ePI","description":"p(ePI) (Preprocessed ePI) is the intermediate state of an ePI after semantic annotations have been embedded by Preprocessors, preparing it for Lens execution.","permalink":"/reference/p-epi"},{"id":"preprocessor","title":"Preprocessor","description":"A Preprocessor is a pluggable service that semantically annotates raw ePIs using standard terminologies (like SNOMED-CT or ICPC-2) to generate a Preprocessed ePI (p(ePI)).","permalink":"/reference/preprocessor"}],"unlisted":false}}')}}]);